                setEvidenceType(e)
                setCount(c)
                console.log(evidenceTypeRef.current)
                console.log(countRef.current)
                setDisplayChart(true);

                }
            });

    }


    useEffect(() => {
        getCity();
    }, []);


    return (
        <Container fluid>
            <Row>
                <Col md={6}>
                    <h1>Crime Trends</h1>
                </Col>
            </Row>
            <Row>
                <Col md={3}>
                    <h5>Select City</h5>
                    <Button type="submit" class="btn btn-primary">
                        Enter
				    </Button>
                </Col>
		        <Col md={3}>
			        <Form>
                        <Form.Select as="select">
                            <option>Select...</option>
                            {city.length && city.map((item, index)=> (
                                <option value={index}>{item.city}</option>
                            ))}
                        </Form.Select>
                    </Form>
                </Col>
                <Col md={6}>
                   <Image src={img}/> 
                </Col>
            </Row>
        </Container>
    )
}
