import React, {useEffect} from 'react';
import useState from 'react-usestateref'
import img from '../images/police.jpeg';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Pie } from 'react-chartjs-2';
import { Container, Form, Row, Col, Image, Button} from 'react-bootstrap';

const axios = require('axios');
ChartJS.register(ArcElement, Tooltip, Legend);

function EvidenceTrends () {
    const [station, setStation] = useState([{}]);
    const [selectedStation, setSelectedStation] = useState();
    const [output, setOutput] = useState();
    const [displayChart, setDisplayChart]= useState(false);
    ChartJS.register(ArcElement, Tooltip, Legend);

    const[evidenceType, setEvidenceType, ref] = useState();
    let count = [];
    const evidence = [];
    var outputList;


    function submit() {
        axios.get('http://localhost:3005/trends/evidence/station', {
            params: {
                inputStation: selectedStation
            }
        })
            .then((res) => {
                if (res) {
                console.log(res.data)
                setOutput(res.data);
                //console.log(ref.current)
                res.data&&res.data.map((record) =>{
                    evidence.push(record.evidenceType);
                    count.push(record.count);
                    setEvidenceType(evidence)


                });
                console.log(ref.current)
                setDisplayChart(true);

                //console.log(evidence)
                //console.log(count)


                }
            });

    }

    useEffect(() => {
        axios.get('http://localhost:3005/trends/evidence/')
            .then((res) => {
                if (res) {
                    setStation(res.data);
                }
            });
    }, []);


    function onChangeOption (event) {
        setSelectedStation((Number(event.target.value)+1));
    }


    const chartData = {
    labels: ref.current,
    datasets: [
        {
        label: '# of Votes',
        data: [1,2,3,4,5],
        backgroundColor: ['rgba(255, 99, 132, 0.2)'],
        borderColor: ['rgba(255, 159, 64, 1)', ],
        borderWidth: 1,
        },
    ],
    };
    return (
        <Container fluid>
            <Row>
            {displayChart? 
                <Pie data={chartData} />
                :
                null
            }
                <Col md={6}>
                    <h1>Evidence Trends</h1>
                </Col>
            </Row>
            <Row>
                <Col md={3}>
                    <h5>Select Station #</h5>
                    <Button type="submit" class="btn btn-primary" onClick={submit}>
                        Enter
				    </Button>
                </Col>
		        <Col md={3}>
			        <Form>
                        <Form.Select as="select" onChange={onChangeOption}>
                            <option>Select...</option>
                            {
                            station && station.map((item, index)=> (
                                <option value={index}>{item.stationNo}</option>
                            ))}
                        </Form.Select>
                    </Form>
                </Col>
                <Col md={6}>
                   <Image src={img}/> 
                </Col>
                {}
            </Row>

        </Container>
    )
}
export default EvidenceTrends;
