import React, {useState, useEffect} from 'react';
import img from '../images/police.jpeg';
import { Container, Form, Row, Col, Image, Button} from 'react-bootstrap';

const axios = require('axios');

export default function CrimeTrends () {


    const [cityList, setCityList] = useState([{}]);

    const getList = () => {
        axios.get('http://localhost:3005/trends/crime/')
        .then((res) => {
            if (res) {
                setCityList(res.data);
            }
        });       
    }

    return (
        <Container fluid>
            <Row>
                <Col md={6}>
                    <h1>Crime Trends</h1>
                </Col>
            </Row>
            <Row>
                <Col md={3}>
                    <h5>Select City</h5>
                    <Button type="submit" class="btn btn-primary">
					    Submit
				    </Button>
                </Col>
		        <Col md={3}>
			        <Form>
                        <Form.Select as="select" value={this.state.inputval} onChange={this.updateinputval}>
                            {cityList.map((item, index)=> (
                                <option value={index}>{item}</option>
                            ))}
                        </Form.Select>
                        <Form.Select aria-label="Default select example">
                            <option>Select...</option>
                            <option value="1">{cityList}</option>
                            <option value="2">Two</option>
                            <option value="3">Three</option>
                        </Form.Select>
                    </Form>
                </Col>
                <Col md={6}>
                   <Image src={img}/> 
                </Col>
            </Row>
        </Container>
    )
}
