import React, {useState, useEffect} from 'react';
import img from '../images/police.jpeg';
import { Container, Form, Row, Col, Image, Button} from 'react-bootstrap';

const axios = require('axios');
export default function EvidenceTrends () {

    const [station, setStation] = useState([{}]);
    const [selectedStation, setSelectedStation] = useState();

    function submit() {
        axios.get('http://localhost:3005/trends/evidence')
            .then((res) => {
                if (res) {
                    setStation(res.data);
                }
            });
    }


    useEffect(() => {
        axios.get('http://localhost:3005/trends/evidence/')
            .then((res) => {
                if (res) {
                    setStation(res.data);
                }
            });
    }, []);


    function onChangeOption (event) {
        console.log(event.target.value)
        setSelectedStation(event.target.value)
    }

    return (
        <Container fluid>
            <Row>
                <Col md={6}>
                    <h1>Evidence Trends</h1>
                </Col>
            </Row>
            <Row>
                <Col md={3}>
                    <h5>Select Station #</h5>
                    <Button type="submit" class="btn btn-primary" onClick={submit}>
                        Enter
				    </Button>
                </Col>
		        <Col md={3}>
			        <Form>
                        <Form.Select as="select">
                            <option>Select...</option>
                            {
                            station.length && station.map((item, index)=> (
                                <option onChange={onChangeOption} value={index}>{item.stationNo}</option>
                            ))}
                        </Form.Select>
                    </Form>
                </Col>
                <Col md={6}>
                   <Image src={img}/> 
                </Col>
            </Row>
        </Container>
    )
}
