import React, {useState, useEffect} from 'react';
import img from '../images/police.jpeg';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Pie } from 'react-chartjs-2';
import { Container, Form, Row, Col, Image, Button} from 'react-bootstrap';

const axios = require('axios');
ChartJS.register(ArcElement, Tooltip, Legend);

export default function EvidenceTrends () {
    const [chartData, setChartData] = useState({});
    const [station, setStation] = useState([{}]);
    const [selectedStation, setSelectedStation] = useState();
    const [output, setOutput] = useState();

    const state = {
        labels: ['Red', 'Blue', 'Yellow'],
        datasets: [
        {
            label: '# of Votes',
            data: [12, 19, 3],
            backgroundColor: ['rgba(255, 99, 132, 0.2)','rgba(54, 162, 235, 0.2)','rgba(255, 206, 86, 0.2)',],
            borderColor: ['rgba(255, 99, 132, 1)','rgba(54, 162, 235, 1)','rgba(255, 206, 86, 1)',],
            borderWidth: 1,
        },
        ]
      }

    function submit() {
        console.log(selectedStation);
        axios.get('http://localhost:3005/trends/evidence/station', {
            params: {
                inputStation: selectedStation
            }
        })
            .then((res) => {
                if (res) {
                    setOutput(res.data);
                    console.log(res.data["count"])
                }
            });
    }

    function setChart() {
        this.setState ({

            labels: [],
            datasets: [
                {
                    label: '',
                    data: [],
                    backgroundColor: [],
                    borderColor: [],
                    borderWidth: 1,
                },
            ]
        });
    }


    useEffect(() => {
        axios.get('http://localhost:3005/trends/evidence/')
            .then((res) => {
                if (res) {
                    setStation(res.data);
                }
            });
    }, []);


    function onChangeOption (event) {
        setSelectedStation((Number(event.target.value)+1));
    }

    return (
        <Container fluid>
            <Row>

                <Col md={6}>
                    <h1>Evidence Trends</h1>
                </Col>
            </Row>
            <Row>
                <Col md={3}>
                    <h5>Select Station #</h5>
                    <Button type="submit" class="btn btn-primary" onClick={submit}>
                        Enter
				    </Button>
                </Col>
		        <Col md={3}>
			        <Form>
                        <Form.Select as="select" onChange={onChangeOption}>
                            <option>Select...</option>
                            {
                            station && station.map((item, index)=> (
                                <option value={index}>{item.stationNo}</option>
                            ))}
                        </Form.Select>
                    </Form>
                </Col>
                <Col md={6}>
                   <Image src={img}/> 
                </Col>
                <Pie
                    data={state}
                    options={{
                        title:{
                        display:true,
                        text:'Average Rainfall per month',
                        fontSize:20,
                        },
                        legend:{
                        display:true,
                        position:'right'
                        },
                        radius: 300,

                    }}
                />
            </Row>

        </Container>
    )
}
